mapper.select:
SELECT BOARD2.* FROM( SELECT COUNT(*) OVER() AS totalRecordCount, BOARD1.* FROM( SELECT ROW_NUMBER() OVER (ORDER BY BNH_NO ASC) AS rnum
,A.BNH_NO ,A.BNH_LOCATION ,A.DEP_CODE ,B.DEP_NM ,A.BNH_LANDLINE ,A.BNH_BUSINESS_HOURS
FROM BRANCH A LEFT OUTER JOIN DEPARTMENT B ON A.DEP_CODE = B.DEP_CODE
WHERE 1=1
/* 지점위치 검색 */ AND BNH_LOCATION LIKE '%' || #{searchInput1} || '%'
/* 부서명 검색 */ AND DEP_NM LIKE '%' || #{searchInput1} || '%'
) BOARD1 ) BOARD2 WHERE BOARD2.RNUM BETWEEN #{start} AND #{end}
